document.addEventListener('DOMContentLoaded', function () {
    // Mobile menu toggle
    const hamburger = document.querySelector('.hamburger');
    const navLinks = document.querySelector('.nav-links');

    hamburger.addEventListener('click', () => {
        navLinks.classList.toggle('active');
    });

    // Cart functionality
    const cartCountElement = document.querySelector('.cart-count');

    // Sample product data (could come from API)
    const products = [
        {
            id: 1,
            name: "Midnight Whisper",
            description: "A bold, mysterious fragrance with notes of amber and oud",
            price: 89.99,
            image: "data:image/jpeg;base64,"
        },
        {
            id: 2,
            name: "Morning Dew",
            description: "Fresh and uplifting with citrus and green tea notes",
            price: 75.99,
            image: "data:image/jpeg;base64,"
        },
        {
            id: 3,
            name: "Golden Amber",
            description: "Warm and sensual with vanilla and sandalwood",
            price: 95.99,
            image: "data:image/jpeg;base64,"
        },
        {
            id: 4,
            name: "Ocean Breeze",
            description: "Crisp aquatic notes with marine accords",
            price: 82.99,
            image: "data:image/jpeg;base64,"
        },
        {
            id: 5,
            name: "Veloura Mist ",
            description: "Crisp aquatic notes with marine accords",
            price: 80.99,
            image: "data:image/jpeg;base64,"
        },
        {
            id: 6,
            name: "Noir Bloom",
            description: "Crisp aquatic notes with marine accords",
            price: 100.99,
            image: "data:image/jpeg;base64,"
        },
        {
            id: 7,
            name: "Amber Whisper",
            description: "Crisp aquatic notes with marine accords",
            price: 56.99,
            image: "data:image/jpeg;base64,"
        },
        {
            id: 8,
            name: "Éclat D’Amour",
            description: "Crisp aquatic notes with marine accords",
            price: 90.99,
            image: "data:image/jpeg;base64,"
        },
        {
            id: 9,
            name: "Celestia Aura",
            description: "Crisp aquatic notes with marine accords",
            price: 99.99,
            image: "data:image/jpeg;base64,"
        }
    ];

    // Render products
    const productsContainer = document.getElementById('products-container');

    function renderProducts() {
        productsContainer.innerHTML = '';

        products.forEach(product => {
            const productCard = document.createElement('div');
            productCard.classList.add('product-card');

            productCard.innerHTML = `
                <div class="product-image">
                    <img src="${product.image}" alt="${product.name}">
                </div>
                <div class="product-info">
                    <h3>${product.name}</h3>
                    <p>${product.description}</p>
                    <div class="product-price">$${product.price.toFixed(2)}</div>
                    <button class="add-to-cart" data-id="${product.id}">Add to Cart</button>
                </div>
            `;

            productsContainer.appendChild(productCard);
        });

        // Attach add-to-cart events
        document.querySelectorAll('.add-to-cart').forEach(button => {
            button.addEventListener('click', function () {
                const productId = parseInt(this.getAttribute('data-id'));
                addToCart(productId);
            });
        });
    }

    // Add to Cart with localStorage
    function addToCart(productId) {
        let cart = JSON.parse(localStorage.getItem('cart')) || [];

        const product = products.find(p => p.id === productId);
        const existingProduct = cart.find(item => item.id === productId);

        if (existingProduct) {
            existingProduct.quantity += 1;
        } else {
            cart.push({ ...product, quantity: 1 });
        }

        localStorage.setItem('cart', JSON.stringify(cart));
        updateCartCount();

        // Feedback animation
        const cartBtn = document.querySelector('.cart-btn');
        cartBtn.classList.add('pulse');
        setTimeout(() => {
            cartBtn.classList.remove('pulse');
        }, 300);
    }

    // Update cart count
    function updateCartCount() {
        const cart = JSON.parse(localStorage.getItem('cart')) || [];
        const totalCount = cart.reduce((sum, item) => sum + item.quantity, 0);
        cartCountElement.textContent = totalCount;
    }

    // Newsletter form
    const newsletterForm = document.getElementById('newsletter-form');
    newsletterForm.addEventListener('submit', function (e) {
        e.preventDefault();
        const emailInput = this.querySelector('input[type="email"]');

        if (emailInput.value) {
            alert(`Thank you for subscribing with ${emailInput.value}!`);
            emailInput.value = '';
        }
    });

    // Sign-up form
    const signupForm = document.getElementById('signup-form');
    signupForm.addEventListener('submit', function (e) {
        e.preventDefault();

        const nameInput = this.querySelector('input[type="text"]');
        const emailInput = this.querySelector('input[type="email"]');
        const passwordInput = this.querySelector('input[type="password"]');

        if (nameInput.value && emailInput.value && passwordInput.value) {
            alert(`Welcome, ${nameInput.value}! Your account has been created.`);
            nameInput.value = '';
            emailInput.value = '';
            passwordInput.value = '';
        }
    });

    // Init
    renderProducts();
    updateCartCount();
});


